//Ophalen endpoints afhankelijk van omgeving def projectProperty = testCase.testSuite.project.getPropertyValue("Omgeving") def endpointProperty = testCase.testSuite.project.getPropertyValue("Endpoints") String dir = "" log.info projectProperty
if (projectProperty == 'O') { log.info 'Dev omgeving' dir = endpointProperty + "EndpointsDev.csv" //Zetten van project naam (toevoeging dev of Acc) testRunner.testCase.testSuite.project.name = "1. NS Flex Bestellen v2.0 (Development)" } else { log.info 'Accept omgeving' dir = endpointProperty + "EndpointsAcc.csv" //Zetten van project naam (toevoeging dev of Acc) testRunner.testCase.testSuite.project.name = "1. NS Flex Bestellen v2.0 (Acceptatie)" } context.fileReader = new BufferedReader(new FileReader(dir))
//Ignore firstline of csv file context.fileReader.readLine() //Read in the second line of the data file secondLine = context.fileReader.readLine()
//Split the first line into a string array and assign the array elements to various test case properties String[] propData = secondLine.split(";")
//Get Endpoints testCase.setPropertyValue("endpoint_new_customer",propData[0]) testCase.setPropertyValue("endpoint_get_product_catalog",propData[1]) testCase.setPropertyValue("endpoint_check_customer",propData[2]) testCase.setPropertyValue("endpoint_create_card",propData[3]) testCase.setPropertyValue("endpoint_get_customer",propData[4]) testCase.setPropertyValue("endpoint_get_proces",propData[5]) testCase.setPropertyValue("endpoint_validate_create_order",propData[6]) testCase.setPropertyValue("endpoint_create_order",propData[7]) testCase.setPropertyValue("endpoint_ideal_status_betalen",propData[8]) testCase.setPropertyValue("endpoint_ophalen_order_status_init",propData[9]) testCase.setPropertyValue("endpoint_ophalen_betaalkenmerk",propData[10]) testCase.setPropertyValue("endpoint_ophalen_order_status_lgstrt",propData[11]) testCase.setPropertyValue("endpoint_ophalen_order_status_afgehandeld",propData[12]) testCase.setPropertyValue("endpoint_ophalen_order_nummer",propData[13]) testCase.setPropertyValue("endpoint_ophalen_nal",propData[14]) testCase.setPropertyValue("soapui_username",propData[22]) testCase.setPropertyValue("soapui_password",propData[23])
