//Ophalen endpoints afhankelijk van omgeving
def projectProperty = testCase.testSuite.project.getPropertyValue("Omgeving")
def endpointProperty = testCase.testSuite.project.getPropertyValue("Endpoints")
String dir = ""
log.info projectProperty

if (projectProperty == 'O')
{
	log.info 'Dev omgeving'
	dir = endpointProperty + "EndpointsDev.csv"
}
else {
	log.info 'Accept omgeving'
	dir = endpointProperty + "EndpointsAcc.csv"
}
context.fileReader = new BufferedReader(new FileReader(dir))

//Ignore firstline of csv file
context.fileReader.readLine()
//Read in the second line of the data file
secondLine = context.fileReader.readLine()

//Split the first line into a string array and assign the array elements to various test case properties
String[] propData = secondLine.split(";")

//Get Endpoints
testCase.setPropertyValue("endpoint_new_customer",propData[0])
testCase.setPropertyValue("endpoint_get_product_catalog",propData[1])
testCase.setPropertyValue("endpoint_check_customer",propData[2])
