//Ophalen endpoints afhankelijk van omgeving def projectProperty = testCase.testSuite.project.getPropertyValue("Omgeving") def endpointProperty = testCase.testSuite.project.getPropertyValue("Endpoints") def versieProperty = testCase.testSuite.project.getPropertyValue("Versie") String dir = "" log.info projectProperty
if (projectProperty == 'O') { log.info "1. NS Flex Bestellen " + versieProperty + " (TA Development)" log.info "TestSuite - NS Flex order nieuwe klant en NSEXTRA " + versieProperty + " (TA Development)" log.info "NS Flex - Bestellen " + testRunner.testCase.testSuite.project.getPropertyValue("NSFlexProduct") dir = endpointProperty + "EndpointsDev.csv" //Zetten van project naam (toevoeging dev of Acc) testRunner.testCase.testSuite.project.name = "1. NS Flex Bestellen " + versieProperty + " (TA Development)" testRunner.testCase.testSuite.name = "TestSuite - NS Flex order nieuwe klant en NSEXTRA " + versieProperty + " (TA Development)" } else { log.info "1. NS Flex Bestellen " + versieProperty + " (TA Acceptatie)" log.info "TestSuite - NS Flex order nieuwe klant en NSEXTRA " + versieProperty + " (TA Acceptatie)" log.info "NS Flex - Bestellen " + testRunner.testCase.testSuite.project.getPropertyValue("NSFlexProduct") dir = endpointProperty + "EndpointsAcc.csv" //Zetten van project naam (toevoeging dev of Acc) testRunner.testCase.testSuite.project.name = "1. NS Flex Bestellen " + versieProperty + " (TA Acceptatie)" testRunner.testCase.testSuite.name = "TestSuite - NS Flex order nieuwe klant en NSEXTRA " + versieProperty + " (TA Acceptatie)"
} context.fileReader = new BufferedReader(new FileReader(dir))
