def product = response [ "//Order_Information//Description" ] as String
def date = new Date() as String

def groovyUtils1 = new com.eviware.soapui.support.GroovyUtils( context )  
def response1 = groovyUtils.getXmlHolder( "New Customer - Aanmaken nieuwe klant#Response" )
def email = response1[ "//ns1:customer//ns1:email//ns1:email_address" ] as String

def groovyUtils2 = new com.eviware.soapui.support.GroovyUtils( context )  
def response2 = groovyUtils.getXmlHolder( "Ophalen status van order (Afgehandeld)#Response" )
def status = response2[  "//bestellingen//kop//orderstatus" ] as String


f.append(date + ";" + product + ";" + referentie + ";" + status + ";" + engravedid + ";" + nsr + ";" + email + "\r\n")
//Voor E2E testing (alleen op testserver)
if(logProperty.startsWith( "C:\\"))
{
	fe2e.write(email + ",")
}

log.info (  product + " " + "met referentienr: " + referentie + " heeft status: Afgehandeld" )
log.info ( "Gegevens van de order zijn succesvol opgeslagen in de logfile op de G-schijf")

