String strPogingen = testRunner.testCase.getPropertyValue("RetryCount")
/*int maxPogingen = Integer.parseInt(strPogingen)*/
int maxPogingen = strPogingen.toInteger()
newStrPogingen = ""

if(status != "Initiële bestelling"){
	maxPogingen = maxPogingen + 1
	newStrPogingen = String.valueOf(maxPogingen)
	testRunner.testCase.setPropertyValue("RetryCount", newStrPogingen)

//Indien niet wordt voldaan aan de status mag er max 10x geprobeerd worden anders in foutstatus
    	if(maxPogingen <100){
    		log.info ("Poging " + maxPogingen)
    		testRunner.gotoStepByName("Delay initiële bestelling")
            	} else {
            		//testRunner.cancel("Meer dan " + newStrPogingen + " keer de status " + status + " proberen op te halen zonder succes!")
            		log.info ("Meer dan " + newStrPogingen + " keer de status " + status + " proberen op te halen zonder succes!")
            		log.info ("Helaas is het niet gelukt om de order aan te maken")
            		log.info ("Bekijk in BD87 of er een idoc niet verwerkt is")
            		testRunner.testCase.setPropertyValue("RetryCount", "0")   
            		testRunner.fail("Meer dan " + newStrPogingen + " keer de status " + status + " proberen op te halen zonder succes!")
              }
}

//Indien wordt voldaan aan de status wordt de teller RetryCount weer naar nul gezet
if(status == "Initiële bestelling"){
	testRunner.testCase.setPropertyValue("RetryCount", "0")
}

