                               case '000':
                                               maxPogingen = maxPogingen + 1
                                               newStrPogingen = String.valueOf(maxPogingen)
                                               testRunner.testCase.setPropertyValue("RetryCount", newStrPogingen)
                                               log.info ("Dit is poging " + maxPogingen + "/10 voor het controleren van de eindstatus van C09-proces")                                          
                                               testRunner.gotoStepByName( "Delay")
                                               break

                               case '010':
                                               maxPogingen = maxPogingen + 1
                                               newStrPogingen = String.valueOf(maxPogingen)
                                               testRunner.testCase.setPropertyValue("RetryCount", newStrPogingen)
                                               log.info ("Dit is poging " + maxPogingen + "/10 voor het controleren van de eindstatus van C09-proces")           
                                               testRunner.gotoStepByName( "Delay")                                                                                            
                                               break                    

                               case '020':
                                               log.info context.expand("Bestelling klaar om op te halen")
                                               testRunner.testCase.setPropertyValue("RetryCount", "0")
                                               testRunner.gotoStepByName( "Haal NSR op")
                                               break

                               case '030':
                                               maxPogingen = maxPogingen + 1
                                               newStrPogingen = String.valueOf(maxPogingen)
                                               testRunner.testCase.setPropertyValue("RetryCount", newStrPogingen)
                                               log.info ("Dit is poging " + maxPogingen + "/10 voor het controleren van de eindstatus van C09-proces")           
                                               testRunner.gotoStepByName( "Delay")                                                                                            
                                               break              

                               default: testRunner.cancel("Niet toegestane BAM-status")
}

//Indien de limiet bereikt is faalt de TestCase
                } else {
            testRunner.cancel("Het maxium aantal pogingen om een eindstatus op te halen is bereikt (C67)!") 
