import org.apache.commons.lang.time.DateUtils

//Ophalen Response en tellen van het aantal sap_product_id's dat door de loop moet lopen
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def response = groovyUtils.getXmlHolder("Geproduceerd - MIOS_NSC0150_ZBOM_Distributieorder_Lezen#response")
testRunner.testCase.setPropertyValue( "count", response["count(//item/product/sap_product_id)"])

String countString = context.expand('${#TestCase#count}')
int count = Integer.parseInt(countString)


//Start van de loop voor het bepalen van de relevante sap_product_id's voor de SCMP_log
//Let op: int i wordt ook gebruikt voor sequence number van de ET-code op de kaart
for(int i = 1; i <= count; i++){

//Bepalen van het commercieel product van het betreffende product en het uitsluiten van  CRD en KRT op basis van Sap_product_id
testRunner.testCase.setPropertyValue( "sap_product_id", response["//item[" + String.valueOf(i) + "]/product/sap_product_id"])

//Let op sap_product_id's kunnen per omgeving afwijken!
if (context.expand( '${#TestCase#sap_product_id}') == "5019" || context.expand( '${#TestCase#sap_product_id}') == "100000160" || context.expand( '${#TestCase#sap_product_id}') == "200001042" || context.expand( '${#TestCase#sap_product_id}') == "OVCHIPKAART" ){
	log.info context.expand( "In iteratie " + String.valueOf(i) + "  van de loop is de regel overgeslagen met SAP product ID " + '${#TestCase#sap_product_id}') 
}

else{
	log.info context.expand( "In iteratie " + String.valueOf(i) + " van de loop is de regel verwerkt met SAP product ID " + '${#TestCase#sap_product_id}') 


//Op basis van een dynamische X-Path expressie de relevante variabelen ophalen
	String ETProductId = testRunner.testCase.setPropertyValue( "ETProductId", response["//item["+ i +"]/product/product_reference_code"])
	ETProductId = context.expand('${#TestCase#ETProductId}')
	String ETProductClass = ""
	String productId = context.expand('${#TestCase#sap_product_id}')
	String ETProductStartDateNew = ""
	String ETProductEndDateNew = ""
	String currentDate = new Date().format("yyyy-MM-dd")
	String datePlusOneYear = DateUtils.addYears(new Date(), 1).format("yyyy-MM-dd")


	if(productId == "ET550" || productId == "ET551"){
				testRunner.testCase.setPropertyValue( "ETProductClass", "U")
				ETProductClass = context.expand('${#TestCase#ETProductClass}')
				ETProductStartDateNew = currentDate
				log.info ETProductStartDateNew
				ETProductEndDateNew = "2029-31-12"
				log.info ETProductEndDateNew
	}

	else {
	ETProductClass = testRunner.testCase.setPropertyValue( "ETProductClass", response["//item["+ i +"]/product/et_reisklasse"])

		//Omhangen van de geschreven klasse uit response naar numerieke klasse voor request
		if (context.expand( '${#TestCase#ETProductClass}') == "SecondClass"){
				testRunner.testCase.setPropertyValue( "ETProductClass", "2")
				ETProductClass = context.expand('${#TestCase#ETProductClass}')
			}

			else if (context.expand( '${#TestCase#ETProductClass}') == "FirstClass"){
				testRunner.testCase.setPropertyValue( "ETProductClass", "1")
				ETProductClass = context.expand('${#TestCase#ETProductClass}')
			}
			else {
				testRunner.testCase.setPropertyValue("ETProductClass", "U")
				ETProductClass = context.expand('${#TestCase#ETProductClass}')
			}


	String ETProductStartDate = testRunner.testCase.setPropertyValue( "ETProductStartDate", response["//item["+ i +"]/product/geldig_vanaf"])
		//Datum formaat aanpassen van yyyyMMdd naar yyyy-MM-dd
		ETProductStartDate = context.expand('${#TestCase#ETProductStartDate}')
		ETProductStartDateNew = new StringBuilder(ETProductStartDate).insert(4, "-").insert(7, "-")


	String ETProductEndDate = testRunner.testCase.setPropertyValue( "ETProductEndDate", response["//item["+ i +"]/product/geldig_tot"])
		//Datum formaat aanpassen van yyyyMMdd naar yyyy-MM-dd
		ETProductEndDate = context.expand('${#TestCase#ETProductEndDate}')
		ETProductEndDateNew = new StringBuilder(ETProductEndDate).insert(4, "-").insert(7, "-")
	}

//Opbouwen XML-bericht met de verkregen variabelen
	String s = "<contract ref="+"\"1\" "+"template=\"" + ETProductId + "\" isUsed="+"\"0\" "+"pref="+"\"2\""+">"+
				"<provider>4</provider>"+
				"<tariff>" + ETProductId +"</tariff>"+
				"<serialNum>" + i + "</serialNum>"+
				"<passengerInfo>"+
					"<class>" + ETProductClass + "</class>"+
				"</passengerInfo>"+
				"<chronologicalValidity>"+
					"<startDt>" + ETProductStartDateNew + "</startDt>"+
					"<endDt>" + ETProductEndDateNew + "</endDt>"+
					"<duration>0</duration>"+
				"</chronologicalValidity>"+
				"<contractSaleData>"+
					"<contractSaleDate>2016-08-18</contractSaleDate>"+
					"<contractSaleTime>07:58:00</contractSaleTime>"+
					"<contractSaleAgent>4</contractSaleAgent>"+
					"<contractSaleDevice>1E000B</contractSaleDevice>"+
				"</contractSaleData>"+
				"<contractPlusVia viaCount="+"\"0\""+"/>"+
				"<usn>0</usn>"+
				"<contractCounter>"+
					"<status>33</status>"+
				"</contractCounter>"+
			"</contract>"

//Wegschrijven productContract XML uit iteratie i
	String concatProductName = "productContract" + i
	testRunner.testCase.setPropertyValue(concatProductName, s)
	}
}


