testRunner.testCase.properties.each{
	testProperty = it.value.name
	testPropertyValue = it.value.value

	if(testProperty.contains("productContract")){
		fileProperty = testPropertyValue
		propArray.add(fileProperty)
	}
}

StringBuilder strBuilder = new StringBuilder()
for (int x = 0; x < propArray.size; x++){
	strBuilder.append(propArray[x])
}

String propString = strBuilder.toString()
testRunner.testCase.setPropertyValue("All_ProductContracts", propString)

