//Switch voor bepalen vervolgstap CPM proces op basis van CPM Orderstatus in SAP CRM
switch (context.expand('${#TestCase#OrderStatus}')){ case 'OPEN': log.info "CPM-Order heeft status 'Open'" testRunner.cancel("Status niet correct") break case 'VALI': log.info "CPM-Order heeft status 'Gevalideerd'" testRunner.gotoStepByName("Verzonden kaartstraat - MIOS_NSC0150_ZBOM_Distributieorder_Lezen") break case 'CANC': log.info "CPM-Order heeft status 'Geannuleerd'" testRunner.cancel("Status niet correct") break case 'AFGV': log.info "CPM-Order heeft status 'Fout voor verzending kaartstraat'" testRunner.cancel("Status niet correct") break case 'SENK': log.info "CPM-Order heeft status 'Verzonden aan kaartstraat'" testRunner.gotoStepByName("Verzonden kaartstraat - MIOS_NSC0150_ZBOM_Distributieorder_Lezen") break case 'AFGK': log.info "CPM-Order heeft status 'Afgekeurd door kaartstraat'" testRunner.cancel("Status niet correct") break case 'HERK': log.info "CPM-Order heeft status 'Herzenden aan kaartstraat'" testRunner.gotoStepByName("Groovy Script - Controle start Verzendjob") break case 'GEPR': log.info "CPM-Order heeft status 'Geproduceerd'" testRunner.gotoStepByName("Get_Order - MIOS_NSC0150_ZBOM_Distributieorder_Lezen") break case 'VERR': log.info "CPM-Order heeft status 'Gegevens verrijkt'" testRunner.gotoStepByName("Get_Order - MIOS_NSC0150_ZBOM_Distributieorder_Lezen") break case 'AFGC': log.info "CPM-Order heeft status 'Fout voor verzending TLS'" testRunner.cancel("Status niet correct") break case 'STLS': log.info "CPM-Order heeft status 'Verzonden aan TLS'" testRunner.gotoStepByName("Get_Order - MIOS_NSC0150_ZBOM_Distributieorder_Lezen") break case 'AFGT': log.info "CPM-Order heeft status 'Afgekeurd door TLS'" testRunner.cancel("Status niet correct") break case 'HERZ': log.info "CPM-Order heeft status 'Herzenden aan TLS'" testRunner.gotoStepByName("Groovy Script - Controle start Verzendjob") break case 'AFGH': log.info "CPM-Order heeft status 'Geregistreerd bij TLS - Einde Productieproces'" break
default: log.info "Onbekende status" testRunner.cancel("Onbekende status") break }

/*Read in the next line of the file We can use the same fileReader created in the Setup script because it was assigned to the context variable.*/
